import React, { useEffect, useRef, useState } from 'react';
import SocketIOClient from 'socket.io-client';

const App = () => {
  const socket = useRef(null);
  const [userName, setUserName] = useState("");
  const [message, setMessage] = useState("");



  const handleConnect = () => {

  }

  useEffect(() => {
    socket.current = SocketIOClient();

    socket.current.on("connect", handleConnect);

    socket.current.on("disconnect", () => console.log("disconnected!"));

    socket.current.on("message", console.log);

    return () => {
      socket.current.removeAllListeners();
    }
  }, []);

  const handleSocket = () => {
    socket.current.emit('message', {message: "Hi there!"})
  }

  const handleRegular = () => {
    fetch("/api/world", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({message: "Hi There!!xD"})
    }).then(res => res.json()).then(console.log);
  }

  return (
    <>
      <input type="text" placeholder="Username" />
      <input type="text" placeholder="Message" />
      <button onClick={handleSocket}>Send me a socket message!</button>
      <button onClick={handleRegular}>Send me a regular message!</button>
    </>
  )
}

export default App;
