import React, { useEffect, useRef, useState } from 'react';
import SocketIOClient from 'socket.io-client';

const App = () => {
  const socket = useRef(null);
  const [userName, setUserName] = useState("");
  const [message, setMessage] = useState("");
  const [online, setOnline] = useState(false);
  const [messages, setMessages] = useState([]);

  const handleUserNameChange = event => {
    setUserName(() => event.target.value);
  }

  const handleMessageChange = event => {
    setMessage(() => event.target.value);
  }

  const handleMessages = (msg) => {
    setMessages(messages => [...messages, msg]);
  }

  const handleConnect = () => {
    setOnline(true);
  }

  const handleDisconnect = () => {
    setOnline(false);
  }

  console.log(messages);

  useEffect(() => {
    socket.current = SocketIOClient();

    socket.current.on("connect", handleConnect);

    socket.current.on("disconnect", () => handleDisconnect);

    socket.current.on("chat message", handleMessages);

    return () => {
      socket.current.removeAllListeners();
    }
  }, []);

  const handleSocket = () => {
    socket.current.emit('chat message', {message: "Hi there!"})
  }

  const handleRegular = () => {
    fetch("/api/world", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({message: "Hi There!!xD"})
    }).then(res => res.json()).then(console.log);
  }

  return (
    <>
      <input type="text" value={userName} onChange={handleUserNameChange} placeholder="Username" />
      <input type="text" value={message} onChange={handleMessageChange} placeholder="Message" />
      <button onClick={handleSocket}>Send me a socket message!</button>
      <button onClick={handleRegular}>Send me a regular message!</button>
    </>
  )
}

export default App;
